const DebouncedSearch: React.FC = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<string[]>([]);
  const [isSearching, setIsSearching] = useState(false);

  // Mock API call
  const fetchResults = async (search: string) => {
    setIsSearching(true);
    // Simulate network delay
    await new Promise((resolve) => setTimeout(resolve, 500));
    const mockData = ['Apple', 'Banana', 'Cherry', 'Date', 'Elderberry', 'Fig', 'Grape'];
    setResults(mockData.filter(item => item.toLowerCase().includes(search.toLowerCase())));
    setIsSearching(false);
  };

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (query.trim()) {
        fetchResults(query);
      } else {
        setResults([]);
      }
    }, 500); // 500ms debounce

    return () => clearTimeout(delayDebounceFn);
  }, [query]);

  return (
    <div className="relative w-full max-w-sm">
      <label className="block text-sm font-medium text-gray-700 mb-1">Search Fruits</label>
      <input
        type="text"
        className="w-full border border-gray-300 rounded-lg p-2.5 focus:ring-blue-500 focus:border-blue-500 outline-none"
        placeholder="Type to search..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      {isSearching && <div className="absolute right-3 top-9 text-gray-400 text-sm">...</div>}

      {results.length > 0 && (
        <ul className="absolute z-10 w-full mt-1 bg-white border border-gray-200 rounded-lg shadow-lg max-h-60 overflow-y-auto">
          {results.map((result, idx) => (
            <li key={idx} className="px-4 py-2 hover:bg-blue-50 cursor-pointer text-gray-700">
