import React, { useState, useEffect } from 'react';

interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error' | 'info';
}

const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: Toast['type']) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);

    // Auto remove after 3 seconds
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, 3000);
  };

  const getBgColor = (type: Toast['type']) => {
    switch (type) {
      case 'success': return 'bg-green-500';
      case 'error': return 'bg-red-500';
      default: return 'bg-blue-500';
    }
  };

  return (
    <div className="p-8">
      <div className="space-x-4">
        <button onClick={() => addToast('Success!', 'success')} className="px-4 py-2 bg-green-600 text-white rounded">Success</button>
        <button onClick={() => addToast('Error occurred!', 'error')} className="px-4 py-2 bg-red-600 text-white rounded">Error</button>
        <button onClick={() => addToast('Just info.', 'info')} className="px-4 py-2 bg-blue-600 text-white rounded">Info</button>
      </div>

      <div className="fixed bottom-5 right-5 flex flex-col space-y-3 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`${getBgColor(toast.type)} text-white px-6 py-3 rounded shadow-lg flex items-center justify-between min-w-[200px] animate-bounce-in`}
          >
            <span>{toast.message}</span>
            <button
              onClick={() => setToasts((prev) => prev.filter((t) => t.id !== toast.id))}
              className="ml-4 font-bold hover:text-gray-200"
            >
              ✕
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

import React, { useEffect, useState } from 'react';

export const ThemeToggle: React.FC = () => {
  const [isDark, setIsDark] = useState<boolean>(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem('theme') === 'dark';
    }
    return false;
  });

  useEffect(() => {
    const root = window.document.documentElement;
    if (isDark) {
      root.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      root.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [isDark]);

  return (
    <button
      onClick={() => setIsDark(!isDark)}
      className="p-2 rounded-lg bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500"
      aria-label="Toggle Dark Mode"
    >
      {isDark ? (
        <span className="flex items-center gap-2">☀️ Light Mode</span>
      ) : (
        <span className="flex items-center gap-2">🌙 Dark Mode</span>
      )}
    </button>
  );
};

