const data = ['Apple', 'Banana', 'Cherry', 'Date', 'Elderberry', 'Fig', 'Grape', 'Honeydew'];

const SearchAutocomplete: React.FC = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<string[]>([]);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    if (query.length > 0) {
      const filtered = data.filter((item) =>
        item.toLowerCase().includes(query.toLowerCase())
      );
      setResults(filtered);
      setIsOpen(true);
    } else {
      setResults([]);
      setIsOpen(false);
    }
  }, [query]);

  return (
    <div className="relative w-full max-w-sm mx-auto mt-10">
      <label className="block text-sm font-medium text-gray-700 mb-1">Search Fruits</label>
      <div className="relative">
        <input
          type="text"
          className="w-full border border-gray-300 rounded-lg py-2 pl-4 pr-10 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition-shadow"
          placeholder="Type to search..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
          <svg className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
          </svg>
        </div>
      </div>

      {isOpen && (
        <ul className="absolute z-10 w-full mt-1 bg-white border border-gray-200 rounded-lg shadow-lg max-h-60 overflow-auto">
          {results.length > 0 ? (
            results.map((item, index) => (
              <li
                key={index}
                className="px-4 py-2 hover:bg-indigo-50 cursor-pointer text-gray-700"
                onClick={() => {
                  setQuery(item);
                  setIsOpen(false);
                }}
              >
                {item}
              </li>
            ))
          ) : (
            <li className="px-4 py-2 text-gray-500 text-sm">No results found.</li>
          )}
        </ul>
      )}
    </div>
  );
};

export default SearchAutocomplete;

