import React, { useState, KeyboardEvent } from 'react';

export const TagInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>([]);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && input.trim()) {
      e.preventDefault();
      if (!tags.includes(input.trim())) {
        setTags([...tags, input.trim()]);
      }
      setInput('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-md">
      <div className="flex flex-wrap gap-2 border border-gray-300 rounded p-2 focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-transparent">
        {tags.map((tag) => (
          <span key={tag} className="bg-blue-100 text-blue-800 text-sm px-2 py-1 rounded flex items-center">
            {tag}
            <button 
              onClick={() => removeTag(tag)}
              className="ml-1 text-blue-600 hover:text-blue-900 focus:outline-none"
            >
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          className="flex-grow outline-none bg-transparent text-gray-700 placeholder-gray-400"
          placeholder="Type and press Enter..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />
      </div>
    </div>
  );
};

import React, { useState } from 'react';

interface StarRatingProps {
  totalStars?: number;
  onRate?: (rating: number) => void;
}

export const StarRating: React.FC<StarRatingProps> = ({ totalStars = 5, onRate }) => {
  const [rating, setRating] = useState(0);
  const [hover, setHover] = useState(0);

  return (
    <div className="flex space-x-1">
      {[...Array(totalStars)].map((_, index) => {
        const starValue = index + 1;
        return (
          <button
            key={index}
            type="button"
            className={`text-2xl focus:outline-none transition-colors duration-200 ${
              starValue <= (hover || rating) ? 'text-yellow-400' : 'text-gray-300'
            }`}
            onClick={() => {
              setRating(starValue);
              if (onRate) onRate(starValue);
            }}
            onMouseEnter={() => setHover(starValue)}
            onMouseLeave={() => setHover(0)}
          >
            ★
          </button>
        );
      })}
    </div>
  );
};

import React, { useState, useEffect } from 'react';

type ToastType = 'success' | 'error' | 'info';

interface ToastProps {
  message: string;
  type: ToastType;
  onClose: () => void;
}

export const Toast: React.FC<ToastProps> = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  const bgColors = {
    success: 'bg-green-500',
    error: 'bg-red-500',
    info: 'bg-blue-500',
  };

  return (
    <div className={`fixed bottom-5 right-5 ${bgColors[type]} text-white px-6 py-3 rounded shadow-lg flex items-center space-x-3 animate-bounce-in`}>
      <span>{message}</span>
      <button onClick={onClose} className="font-bold ml-4 hover:text-gray-200">
        ✕
      </button>
    </div>
  );
};

