import React from 'react';
import { Check } from 'lucide-react';

interface StepperProps {
  currentStep: number; // 1-based index
  steps: string[];
}

const Stepper: React.FC<StepperProps> = ({ currentStep, steps }) => {
  return (
    <div className="w-full py-6">
      <div className="flex items-center justify-between">
        {steps.map((step, i) => {
          const stepNum = i + 1;
          const isCompleted = currentStep > stepNum;
          const isActive = currentStep === stepNum;

          return (
            <React.Fragment key={step}>
              {/* Step Circle & Label */}
              <div className="flex flex-col items-center relative z-10">
                <div
                  className={`flex h-10 w-10 items-center justify-center rounded-full border-2 transition-all duration-300 ${
                    isCompleted
                      ? 'border-indigo-600 bg-indigo-600 text-white'
                      : isActive
                      ? 'border-indigo-600 bg-white text-indigo-600'
                      : 'border-gray-300 bg-white text-gray-400'
                  }`}
                >
                  {isCompleted ? <Check size={20} /> : <span>{stepNum}</span>}
                </div>
                <span
                  className={`mt-2 text-xs font-medium uppercase tracking-wide ${
                    isActive || isCompleted ? 'text-indigo-600' : 'text-gray-400'
                  }`}
                >
                  {step}
                </span>
              </div>

              {/* Connector Line (don't render after last item) */}
              {i !== steps.length - 1 && (
                <div className="flex-auto border-t-2 transition-all duration-300 border-gray-200 mx-2">
                  <div
                    className={`h-0.5 bg-indigo-600 transition-all duration-500 ease-out ${
                      isCompleted ? 'w-full' : 'w-0'
                    }`}
                  ></div>
                </div>
              )}
            </React.Fragment>
          );
        })}
      </div>
    </div>
  );
};

export default Stepper;

