import React, { useState, useEffect } from 'react';

type ToastType = 'success' | 'error' | 'info';

interface Toast {
  id: number;
  message: string;
  type: ToastType;
}

export const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: ToastType) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
  };

  useEffect(() => {
    if (toasts.length > 0) {
      const timer = setTimeout(() => {
        setToasts((prev) => prev.slice(1));
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [toasts]);

  const getBgColor = (type: ToastType) => {
    switch (type) {
      case 'success': return 'bg-green-500';
      case 'error': return 'bg-red-500';
      default: return 'bg-blue-500';
    }
  };

  return (
    <div className="relative h-64 w-full bg-gray-50 border rounded-lg overflow-hidden p-4">
      <div className="flex space-x-2 mb-4">
        <button onClick={() => addToast('Saved successfully!', 'success')} className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">Success</button>
        <button onClick={() => addToast('Something went wrong.', 'error')} className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700">Error</button>
      </div>

      <div className="absolute bottom-4 right-4 flex flex-col space-y-2">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`${getBgColor(toast.type)} text-white px-6 py-3 rounded shadow-lg transform transition-all duration-300 animate-slide-in-right flex items-center`}
          >
            <span className="text-sm font-medium">{toast.message}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';

export const DebouncedSearch: React.FC = () => {
  const [query, setQuery] = useState('');
  const [debouncedQuery, setDebouncedQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);

  // Debounce Logic
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedQuery(query);
      setIsSearching(false);
    }, 500);

    if (query !== debouncedQuery) setIsSearching(true);

    return () => clearTimeout(handler);
  }, [query]);

  return (
    <div className="max-w-md mx-auto p-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">Search Users</label>
      <div className="relative">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Type to search..."
          className="block w-full pl-4 pr-10 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
        />
        {isSearching && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
            <div className="animate-spin h-4 w-4 border-2 border-blue-500 rounded-full border-t-transparent"></div>
          </div>
        )}
      </div>

      <div className="mt-4 p-4 bg-gray-50 rounded border border-gray-200">
        <p className="text-sm text-gray-600">
          <span className="font-bold">Immediate Value:</span> {query}
        </p>
        <p className="text-sm text-gray-600 mt-1">
          <span className="font-bold">Debounced API Call:</span> {debouncedQuery || '...'}
        </p>
      </div>
    </div>
  );
};

