const Stepper: React.FC = () => {
  const [step, setStep] = useState(1);
  const totalSteps = 3;

  const handleNext = () => setStep((prev) => Math.min(prev + 1, totalSteps));
  const handlePrev = () => setStep((prev) => Math.max(prev - 1, 1));

  return (
    <div className="w-full max-w-lg mx-auto p-6">
      {/* Progress Bar */}
      <div className="flex items-center justify-between mb-8">
        {[1, 2, 3].map((num) => (
          <div key={num} className="flex items-center">
            <div
              className={`w-8 h-8 flex items-center justify-center rounded-full font-bold transition-colors duration-300 ${
                step >= num ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-500'
              }`}
            >
              {num}
            </div>
            {num < totalSteps && (
              <div
                className={`w-24 h-1 mx-2 transition-colors duration-300 ${
                  step > num ? 'bg-blue-600' : 'bg-gray-200'
                }`}
              />
            )}
          </div>
        ))}
      </div>

      {/* Content */}
      <div className="bg-white p-6 rounded shadow-sm border border-gray-200 min-h-[150px]">
        {step === 1 && <h2 className="text-xl font-semibold">Step 1: Personal Info</h2>}
        {step === 2 && <h2 className="text-xl font-semibold">Step 2: Account Details</h2>}
        {step === 3 && <h2 className="text-xl font-semibold">Step 3: Confirmation</h2>}
      </div>

      {/* Controls */}
      <div className="flex justify-between mt-6">
        <button
          onClick={handlePrev}
          disabled={step === 1}
          className="px-4 py-2 bg-gray-300 rounded disabled:opacity-50"
        >
          Back
        </button>
        <button
          onClick={handleNext}
          disabled={step === totalSteps}
          className="px-4 py-2 bg-blue-600 text-white rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default Stepper;

