import React, { useState } from 'react';

export const FormWizard = () => {
  const [step, setStep] = useState(1);
  const totalSteps = 3;

  const handleNext = () => setStep((prev) => Math.min(prev + 1, totalSteps));
  const handleBack = () => setStep((prev) => Math.max(prev - 1, 1));

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-xl shadow-md border border-gray-100">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between mb-2">
          {[1, 2, 3].map((item) => (
            <div key={item} className={`text-xs font-semibold uppercase ${step >= item ? 'text-indigo-600' : 'text-gray-400'}`}>
              Step {item}
            </div>
          ))}
        </div>
        <div className="overflow-hidden h-2 mb-4 text-xs flex rounded bg-gray-200">
          <div
            style={{ width: `${(step / totalSteps) * 100}%` }}
            className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-indigo-600 transition-all duration-500"
          ></div>
        </div>
      </div>

      {/* Step Content */}
      <div className="min-h-[200px]">
        {step === 1 && (
          <div className="space-y-4 animate-fade-in">
            <h2 className="text-2xl font-bold text-gray-800">Account Details</h2>
            <input type="email" placeholder="Email Address" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
            <input type="password" placeholder="Password" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
          </div>
        )}
        {step === 2 && (
          <div className="space-y-4 animate-fade-in">
            <h2 className="text-2xl font-bold text-gray-800">Personal Info</h2>
            <input type="text" placeholder="First Name" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
            <input type="text" placeholder="Last Name" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
          </div>
        )}
        {step === 3 && (
          <div className="space-y-4 animate-fade-in text-center">
            <h2 className="text-2xl font-bold text-gray-800">Review & Submit</h2>
            <p className="text-gray-600">Please review your information before submitting.</p>
          </div>
        )}
      </div>

      {/* Navigation Actions */}
      <div className="flex justify-between mt-8">
        <button
          onClick={handleBack}
          disabled={step === 1}
          className={`px-6 py-2 rounded-lg font-medium transition-colors ${step === 1 ? 'bg-gray-100 text-gray-400 cursor-not-allowed' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
        >
          Back
        </button>
        <button
          onClick={handleNext}
          className="px-6 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700 transition-colors shadow-sm"
        >
          {step === totalSteps ? 'Submit' : 'Next'}
        </button>
      </div>
    </div>
  );
};

