import React, { useState, useEffect, useCallback } from 'react';

// Types
type Toast = { id: number; message: string; type: 'success' | 'error' };

const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
  };

  const removeToast = useCallback((id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  }, []);

  // Auto-dismiss logic component
  const ToastItem: React.FC<{ toast: Toast }> = ({ toast }) => {
    useEffect(() => {
      const timer = setTimeout(() => removeToast(toast.id), 3000);
      return () => clearTimeout(timer);
    }, [toast.id]);

    const bgClass = toast.type === 'success' ? 'bg-green-500' : 'bg-red-500';

    return (
      <div className={`${bgClass} text-white px-4 py-3 rounded shadow-lg mb-2 flex justify-between items-center min-w-[250px] animate-in slide-in-from-right`}>
        <span>{toast.message}</span>
        <button onClick={() => removeToast(toast.id)} className="ml-4 font-bold">×</button>
      </div>
    );
  };

  return (
    <div className="p-8">
      <div className="space-x-4">
        <button 
          onClick={() => addToast('Saved successfully!', 'success')} 
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Show Success
        </button>
        <button 
          onClick={() => addToast('Something went wrong.', 'error')} 
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Show Error
        </button>
      </div>

      {/* Toast Container */}
      <div className="fixed bottom-5 right-5 flex flex-col items-end z-50">
        {toasts.map((toast) => (
          <ToastItem key={toast.id} toast={toast} />
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

import React, { useEffect, useState } from 'react';
import { Moon, Sun } from 'lucide-react';

const ThemeToggle: React.FC = () => {
  const [theme, setTheme] = useState<'light' | 'dark'>('light');

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as 'light' | 'dark' | null;
    if (savedTheme) {
      setTheme(savedTheme);
      document.documentElement.classList.toggle('dark', savedTheme === 'dark');
    } else if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
      setTheme('dark');
      document.documentElement.classList.add('dark');
    }
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.classList.toggle('dark', newTheme === 'dark');
  };

  return (
    <button
      onClick={toggleTheme}
      className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 transition-colors duration-200 hover:bg-gray-300 dark:hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
      aria-label="Toggle Dark Mode"
    >
      {theme === 'light' ? <Moon size={20} /> : <Sun size={20} />}
    </button>
  );
};

export default ThemeToggle;

