import React, { useState } from 'react';
import { X, CheckCircle, AlertCircle } from 'lucide-react';

type ToastType = 'success' | 'error';

interface Toast {
  id: number;
  message: string;
  type: ToastType;
}

const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (type: ToastType) => {
    const id = Date.now();
    const message = type === 'success' ? 'Operation successful!' : 'Something went wrong.';
    setToasts((prev) => [...prev, { id, message, type }]);

    // Auto remove after 3 seconds
    setTimeout(() => {
      removeToast(id);
    }, 3000);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  return (
    <div className="p-8">
      <div className="flex gap-4">
        <button
          onClick={() => addToast('success')}
          className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
        >
          Trigger Success
        </button>
        <button
          onClick={() => addToast('error')}
          className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
        >
          Trigger Error
        </button>
      </div>

      {/* Toast Container */}
      <div className="fixed bottom-5 right-5 flex flex-col gap-3 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`flex items-center w-80 p-4 rounded-lg shadow-lg text-white transition-all duration-500 animate-in slide-in-from-right ${
              toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
            }`}
          >
            <div className="mr-3">
              {toast.type === 'success' ? <CheckCircle size={20} /> : <AlertCircle size={20} />}
            </div>
            <div className="flex-1 text-sm font-medium">{toast.message}</div>
            <button
              onClick={() => removeToast(toast.id)}
              className="ml-3 hover:text-gray-200 focus:outline-none"
            >
              <X size={18} />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

