import React, { useState, KeyboardEvent } from 'react';

export const TagInput = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      const trimmed = input.trim();
      if (trimmed && !tags.includes(trimmed)) {
        setTags([...tags, trimmed]);
        setInput('');
      }
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-md">
      <label className="mb-2 block text-sm font-bold text-gray-700 dark:text-gray-200">Add Tags</label>
      <div className="flex flex-wrap gap-2 rounded-lg border border-gray-300 bg-white p-2 focus-within:ring-2 focus-within:ring-blue-500 dark:border-gray-600 dark:bg-gray-800">
        {tags.map(tag => (
          <span key={tag} className="flex items-center gap-1 rounded bg-blue-100 px-2 py-1 text-sm font-medium text-blue-800 dark:bg-blue-900 dark:text-blue-200">
            {tag}
            <button onClick={() => removeTag(tag)} className="text-blue-600 hover:text-blue-800 dark:text-blue-300">
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          className="flex-1 border-none bg-transparent p-1 text-sm outline-none dark:text-white placeholder-gray-400"
          placeholder="Press Enter to add..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />
      </div>
    </div>
  );
};

