}

export const Toast: React.FC<ToastProps> = ({ message, type, onClose }) => {
  const [visible, setVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setVisible(false);
      setTimeout(onClose, 300); // Wait for animation to finish before unmounting
    }, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  const bgColors = {
    success: 'bg-green-500',
    error: 'bg-red-500',
    info: 'bg-blue-500',
  };

  return (
    <div 
      className={`
        flex w-full max-w-xs items-center justify-between rounded-lg p-4 text-white shadow-lg transition-all duration-300
        ${bgColors[type]}
        ${visible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-2'}
      `}
      role="alert"
    >
      <div className="text-sm font-semibold">{message}</div>
      <button onClick={() => setVisible(false)} className="ml-4 opacity-70 hover:opacity-100">
        &times;
      </button>
    </div>
  );
};

