        {tags.map(tag => (
          <span key={tag} className="flex items-center gap-1 px-2 py-1 text-sm bg-indigo-100 text-indigo-700 rounded-md">
            {tag}
            <button onClick={() => removeTag(tag)} className="hover:text-indigo-900 focus:outline-none">
              <X size={14} />
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-1 min-w-[120px] bg-transparent outline-none text-sm text-gray-700 placeholder-gray-400"
          placeholder="Type and press Enter..."
        />
