import React, { useState } from 'react';

const NewsletterForm: React.FC = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState<'idle' | 'error' | 'success'>('idle');

  const validateEmail = (email: string) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateEmail(email)) {
      setStatus('success');
      // Simulate API call
      console.log('Subscribed:', email);
    } else {
      setStatus('error');
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-2">Subscribe to our newsletter</h2>
      <p className="text-gray-500 mb-6">Get the latest updates directly to your inbox.</p>

      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <div className="flex gap-2">
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => { 
              setEmail(e.target.value); 
              if(status !== 'idle') setStatus('idle');
            }}
            className={`flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 transition-all
              ${status === 'error' 
                ? 'border-red-500 focus:ring-red-200' 
                : 'border-gray-300 focus:border-indigo-500 focus:ring-indigo-200'}
            `}
          />
          <button 
            type="submit"
            className="px-6 py-2 bg-indigo-600 text-white font-medium rounded-lg hover:bg-indigo-700 transition-colors"
          >
            Join
          </button>
        </div>

        {status === 'error' && (
          <p className="text-red-500 text-sm mt-1">Please enter a valid email address.</p>
        )}
        {status === 'success' && (
          <p className="text-green-600 text-sm mt-1">Thanks for subscribing!</p>
        )}
      </form>
    </div>
  );
};

export default NewsletterForm;

