import React, { useState } from 'react';

const StepperForm: React.FC = () => {
  const [step, setStep] = useState(1);
  const totalSteps = 3;

  const handleNext = () => {
    if (step < totalSteps) setStep(step + 1);
  };

  const handlePrev = () => {
    if (step > 1) setStep(step - 1);
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-xl shadow-md border border-gray-100">
      {/* Progress Bar */}
      <div className="flex justify-between mb-8">
        {[1, 2, 3].map((num) => (
          <div key={num} className="flex flex-col items-center">
            <div
              className={`w-8 h-8 flex items-center justify-center rounded-full font-bold transition-colors duration-300 ${
                step >= num ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-500'
              }`}
            >
              {num}
            </div>
            <span className="text-xs mt-1 text-gray-500">Step {num}</span>
          </div>
        ))}
      </div>

      {/* Form Content */}
      <div className="mb-8 h-32">
        {step === 1 && (
          <div className="animate-fade-in">
            <h3 className="text-lg font-bold mb-2">Personal Info</h3>
            <input type="text" placeholder="Full Name" className="w-full p-2 border rounded mb-2" />
            <input type="email" placeholder="Email" className="w-full p-2 border rounded" />
          </div>
        )}
        {step === 2 && (
          <div className="animate-fade-in">
            <h3 className="text-lg font-bold mb-2">Shipping Address</h3>
            <input type="text" placeholder="Street Address" className="w-full p-2 border rounded mb-2" />
            <input type="text" placeholder="City" className="w-full p-2 border rounded" />
          </div>
        )}
        {step === 3 && (
          <div className="animate-fade-in">
            <h3 className="text-lg font-bold mb-2">Confirmation</h3>
            <p className="text-gray-600">Please review your details before submitting.</p>
          </div>
        )}
      </div>

      {/* Controls */}
      <div className="flex justify-between">
        <button
          onClick={handlePrev}
          disabled={step === 1}
          className={`px-4 py-2 rounded ${step === 1 ? 'bg-gray-100 text-gray-400 cursor-not-allowed' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
        >
          Previous
        </button>
        <button
          onClick={step === totalSteps ? () => alert('Submitted!') : handleNext}
          className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700"
        >
          {step === totalSteps ? 'Submit' : 'Next'}
        </button>
      </div>
    </div>
  );
};

export default StepperForm;

