import React, { useState, DragEvent } from 'react';
import { UploadCloud } from 'lucide-react';

export const FileUpload: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [files, setFiles] = useState<File[]>([]);

  const handleDragOver = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      setFiles(Array.from(e.dataTransfer.files));
      e.dataTransfer.clearData();
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6">
      <div
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        className={`flex flex-col items-center justify-center w-full h-64 border-2 border-dashed rounded-lg cursor-pointer transition-colors ${isDragging ? 'border-blue-500 bg-blue-50' : 'border-gray-300 bg-gray-50 hover:bg-gray-100'}`}
      >
        <div className="flex flex-col items-center justify-center pt-5 pb-6">
          <UploadCloud className={`w-10 h-10 mb-3 ${isDragging ? 'text-blue-500' : 'text-gray-400'}`} />
          <p className="mb-2 text-sm text-gray-500">
            <span className="font-semibold">Click to upload</span> or drag and drop
          </p>
          <p className="text-xs text-gray-500">SVG, PNG, JPG or GIF (MAX. 800x400px)</p>
        </div>
      </div>
      {files.length > 0 && (
        <ul className="mt-4 space-y-2">
          {files.map((file, idx) => (
            <li key={idx} className="text-sm text-gray-700 bg-gray-100 p-2 rounded">
              {file.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Search } from 'lucide-react';

export const DebouncedSearch: React.FC = () => {
  const [query, setQuery] = useState('');
  const [debouncedQuery, setDebouncedQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);

  // Debounce Logic
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedQuery(query);
      setIsSearching(false);
    }, 500);

    if (query !== debouncedQuery) setIsSearching(true);

    return () => clearTimeout(handler);
  }, [query]);

  return (
    <div className="max-w-md mx-auto mt-10">
      <div className="relative">
        <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
          <Search className="w-5 h-5 text-gray-400" />
        </div>
        <input
          type="text"
          className="block w-full p-4 pl-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 outline-none"
          placeholder="Search items..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        {isSearching && (
          <div className="absolute inset-y-0 right-0 flex items-center pr-3">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
          </div>
        )}
      </div>
      <div className="mt-4 p-4 bg-white border rounded shadow-sm">
        <p className="text-sm text-gray-600">
          Active Query: <span className="font-bold">{query}</span>
        </p>
        <p className="text-sm text-gray-600">
          Debounced Result: <span className="font-bold text-blue-600">{debouncedQuery}</span>
        </p>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { X } from 'lucide-react';

interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error';
}

export const ToastManager: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  return (
    <div className="p-10">
      <div className="space-x-4">
        <button 
          onClick={() => addToast('Operation successful!', 'success')} 
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Show Success
        </button>
        <button 
          onClick={() => addToast('Something went wrong.', 'error')} 
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Show Error
        </button>
      </div>

      <div className="fixed bottom-5 right-5 flex flex-col space-y-3">
        {toasts.map((toast) => (
          <ToastItem key={toast.id} toast={toast} onClose={() => removeToast(toast.id)} />
        ))}
      </div>
    </div>
  );
};

const ToastItem: React.FC<{ toast: Toast; onClose: () => void }> = ({ toast, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  const bgClass = toast.type === 'success' ? 'bg-green-50 border-green-200 text-green-800' : 'bg-red-50 border-red-200 text-red-800';

  return (
    <div className={`flex items-center justify-between w-72 p-4 rounded-lg shadow-lg border ${bgClass} animate-slide-in`}>
      <span className="text-sm font-medium">{toast.message}</span>
      <button onClick={onClose} className="ml-4 hover:opacity-75">
        <X size={16} />
      </button>
    </div>
  );
};

import React, { useState } from 'react';

export const MultiStepForm: React.FC = () => {
  const [step, setStep] = useState(1);
  const totalSteps = 3;

  const nextStep = () => setStep((prev) => Math.min(prev + 1, totalSteps));
  const prevStep = () => setStep((prev) => Math.max(prev - 1, 1));

  return (
    <div className="max-w-lg mx-auto bg-white p-8 rounded-xl shadow-lg border border-gray-100">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between mb-2">
          <span className="text-xs font-semibold text-gray-600">Step {step} of {totalSteps}</span>
          <span className="text-xs font-semibold text-gray-600">{Math.round((step / totalSteps) * 100)}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div 
            className="bg-indigo-600 h-2.5 rounded-full transition-all duration-300"
            style={{ width: `${(step / totalSteps) * 100}%` }}
          ></div>
        </div>
      </div>

      {/* Form Content */}
      <div className="min-h-[200px]">
        {step === 1 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-bold mb-4">Account Details</h2>
            <input type="email" placeholder="Email Address" className="w-full p-3 border rounded mb-3" />
            <input type="password" placeholder="Password" className="w-full p-3 border rounded" />
          </div>
        )}
        {step === 2 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-bold mb-4">Personal Info</h2>
            <input type="text" placeholder="First Name" className="w-full p-3 border rounded mb-3" />
            <input type="text" placeholder="Last Name" className="w-full p-3 border rounded" />
          </div>
        )}
        {step === 3 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-bold mb-4">Review</h2>
            <p className="text-gray-600">Please review your information before submitting.</p>
            <div className="mt-4 p-4 bg-gray-50 rounded">
              <p><strong>Email:</strong> user@example.com</p>
              <p><strong>Name:</strong> John Doe</p>
            </div>
          </div>
        )}
      </div>

      {/* Navigation Buttons */}
      <div className="flex justify-between mt-8">
        <button
          onClick={prevStep}
          disabled={step === 1}
          className={`px-6 py-2 rounded text-sm font-medium ${step === 1 ? 'bg-gray-100 text-gray-400 cursor-not-allowed' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'}`}
        >
          Back
        </button>
        <button
          onClick={step === totalSteps ? () => alert('Submitted!') : nextStep}
          className="bg-indigo-600 text-white px-6 py-2 rounded text-sm font-medium hover:bg-indigo-700 transition"
        >
          {step === totalSteps ? 'Submit' : 'Next'}
        </button>
      </div>
    </div>
  );
};

