import React, { useState, KeyboardEvent } from 'react';

const TagsInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      const trimmed = input.trim();
      if (trimmed && !tags.includes(trimmed)) {
        setTags([...tags, trimmed]);
        setInput('');
      }
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="max-w-md mx-auto mt-10">
      <label className="block text-sm font-medium text-gray-700 mb-2">Add Skills</label>
      <div className="flex flex-wrap items-center gap-2 p-2 border border-gray-300 rounded-md focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-blue-500 bg-white">
        {tags.map((tag) => (
          <span key={tag} className="flex items-center px-2 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
            {tag}
            <button
              onClick={() => removeTag(tag)}
              className="ml-1 text-blue-600 hover:text-blue-900 focus:outline-none"
            >
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-grow outline-none bg-transparent min-w-[120px] text-gray-700 placeholder-gray-400"
          placeholder="Type and press Enter..."
        />
      </div>
    </div>
  );
};

