interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error';
}

const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
    setTimeout(() => removeToast(id), 3000);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  return (
    <div className="p-8">
      <div className="space-x-4">
        <button
          onClick={() => addToast('Operation Successful!', 'success')}
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Success Toast
        </button>
        <button
          onClick={() => addToast('Something went wrong.', 'error')}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Error Toast
        </button>
      </div>

      <div className="fixed top-4 right-4 flex flex-col space-y-3 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`px-4 py-3 rounded shadow-lg text-white flex items-center justify-between min-w-[250px] animate-slide-in ${
              toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
            }`}
          >
            <span>{toast.message}</span>
            <button onClick={() => removeToast(toast.id)} className="ml-4 font-bold">
              &times;
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

