  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      const trimmed = input.trim();
      if (trimmed && !tags.includes(trimmed)) {
        setTags([...tags, trimmed]);
        setInput('');
      }
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-lg">
      <label className="mb-2 block text-sm font-medium text-gray-700">Add Tags</label>
      <div className="flex flex-wrap gap-2 rounded-md border border-gray-300 bg-white p-2 focus-within:border-indigo-500 focus-within:ring-1 focus-within:ring-indigo-500">
        {tags.map((tag) => (
          <span key={tag} className="flex items-center gap-1 rounded bg-indigo-100 px-2 py-1 text-sm text-indigo-800">
            {tag}
            <button 
              onClick={() => removeTag(tag)}
              className="rounded-full p-0.5 hover:bg-indigo-200"
            >
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-1 border-none bg-transparent p-1 text-sm focus:outline-none focus:ring-0"
          placeholder="Press Enter to add..."
        />
      </div>
    </div>
  );
};

export default TagInput;

