// Mock API function
const fakeApiCall = (query: string): Promise<string[]> => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(query ? [`Result for ${query} 1`, `Result for ${query} 2`, `Result for ${query} 3`] : []);
    }, 800);
  });
};

const DebouncedSearch: React.FC = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const timer = setTimeout(async () => {
      if (query.trim()) {
        setIsLoading(true);
        try {
          const data = await fakeApiCall(query);
          setResults(data);
        } finally {
          setIsLoading(false);
        }
      } else {
        setResults([]);
      }
    }, 500);

    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="w-full max-w-md mx-auto mt-10">
      <div className="relative">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search items..."
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition"
        />
        {isLoading && (
          <div className="absolute right-3 top-2.5">
            <div className="animate-spin h-5 w-5 border-2 border-blue-500 rounded-full border-t-transparent"></div>
          </div>
        )}
      </div>

      {results.length > 0 && (
        <ul className="mt-2 bg-white border border-gray-200 rounded-lg shadow-lg overflow-hidden">
          {results.map((item, index) => (
            <li key={index} className="px-4 py-2 hover:bg-gray-50 cursor-pointer text-gray-700 border-b last:border-none border-gray-100">
              {item}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

