import React, { useState, useEffect, useRef } from 'react';

const InfiniteScrollList: React.FC = () => {
  const [items, setItems] = useState<number[]>(Array.from({ length: 20 }, (_, i) => i + 1));
  const [isLoading, setIsLoading] = useState(false);
  const loaderRef = useRef<HTMLDivElement>(null);

  const loadMoreItems = () => {
    setIsLoading(true);
    setTimeout(() => {
      setItems((prev) => [
        ...prev,
        ...Array.from({ length: 10 }, (_, i) => prev.length + i + 1),
      ]);
      setIsLoading(false);
    }, 1000);
  };

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting && !isLoading) {
        loadMoreItems();
      }
    }, { threshold: 1.0 });

    if (loaderRef.current) {
      observer.observe(loaderRef.current);
    }

    return () => observer.disconnect();
  }, [isLoading]);

  return (
    <div className="w-full max-w-lg mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Infinite List</h2>
      <div className="space-y-4">
        {items.map((item) => (
          <div 
            key={item} 
            className="p-6 bg-white rounded-lg shadow-sm border border-gray-200 flex items-center"
          >
            <span className="w-10 h-10 bg-indigo-100 text-indigo-600 rounded-full flex items-center justify-center font-bold mr-4">
              {item}
            </span>
            <span className="text-gray-700">Content Item #{item}</span>
          </div>
        ))}
      </div>

      <div ref={loaderRef} className="h-20 flex items-center justify-center mt-4">
        {isLoading && (
          <div className="flex space-x-2 animate-pulse">
            <div className="w-3 h-3 bg-gray-400 rounded-full"></div>
            <div className="w-3 h-3 bg-gray-400 rounded-full"></div>
            <div className="w-3 h-3 bg-gray-400 rounded-full"></div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InfiniteScrollList;

