import React, { useState, useEffect } from 'react';

interface ToastProps {
  message: string;
  type?: 'success' | 'error' | 'info';
  onClose: () => void;
}

const Toast: React.FC<ToastProps> = ({ message, type = 'info', onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  const bgColors = {
    success: 'bg-green-500',
    error: 'bg-red-500',
    info: 'bg-blue-500',
  };

  return (
    <div className={`fixed bottom-5 right-5 flex items-center w-full max-w-xs p-4 space-x-4 text-white rounded-lg shadow dark:text-gray-400 ${bgColors[type]} transition-opacity duration-300 animate-slide-in`}>
      <div className="text-sm font-normal">{message}</div>
    </div>
  );
};

