import React, { useState, useEffect } from 'react';

// Custom Hook
function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
    return () => clearTimeout(handler);
  }, [value, delay]);

  return debouncedValue;
}

export const SearchComponent: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState<string[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const debouncedSearchTerm = useDebounce(searchTerm, 500);

  useEffect(() => {
    if (debouncedSearchTerm) {
      setIsSearching(true);
      // Simulate API call
      setTimeout(() => {
        setResults([
          `Result for ${debouncedSearchTerm} 1`,
          `Result for ${debouncedSearchTerm} 2`,
          `Result for ${debouncedSearchTerm} 3`,
        ]);
        setIsSearching(false);
      }, 800);
    } else {
      setResults([]);
    }
  }, [debouncedSearchTerm]);

  return (
    <div className="w-full max-w-md mx-auto mt-10">
      <div className="relative">
        <input
          type="text"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-shadow"
          placeholder="Search items..."
          value={searchTerm}
