const OPTIONS = ['React', 'Vue', 'Angular', 'Svelte', 'Node.js'];

export const MultiSelect: React.FC = () => {
  const [selected, setSelected] = useState<string[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const wrapperRef = useRef<HTMLDivElement>(null);

  // Close on click outside
  useEffect(() => {
    function handleClickOutside(event: MouseEvent) {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleOption = (option: string) => {
    if (selected.includes(option)) {
      setSelected(selected.filter(item => item !== option));
    } else {
      setSelected([...selected, option]);
    }
  };

  const removeOption = (e: React.MouseEvent, option: string) => {
    e.stopPropagation();
    setSelected(selected.filter(item => item !== option));
  };

  return (
    <div className="w-full max-w-xs mx-auto" ref={wrapperRef}>
      <label className="block text-sm font-medium text-gray-700 mb-1">Tech Stack</label>
      <div className="relative">
        <div
          className="min-h-[42px] w-full border border-gray-300 rounded-md shadow-sm pl-3 pr-10 py-2 bg-white cursor-pointer flex flex-wrap gap-2 items-center"
          onClick={() => setIsOpen(!isOpen)}
        >
          {selected.length === 0 && <span className="text-gray-400 text-sm">Select options...</span>}
          {selected.map(item => (
            <span key={item} className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-indigo-100 text-indigo-800">
              {item}
              <button
                onClick={(e) => removeOption(e, item)}
                className="ml-1 text-indigo-600 hover:text-indigo-900 focus:outline-none"
              >
                &times;
              </button>
            </span>
          ))}
          <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
             <svg className="h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="currentColor">
               <path fillRule="evenodd" d="M10 3a1 1 0 01.707.293l3 3a1 1 0 01-1.414 1.414L10 5.414 7.707 7.707a1 1 0 01-1.414-1.414l3-3A1 1 0 0110 3zm-3.707 9.293a1 1 0 011.414 0L10 14.586l2.293-2.293a1 1 0 011.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd" />
             </svg>
          </span>
        </div>

        {isOpen && (
