import React, { useState, useEffect } from 'react';

type ToastType = 'success' | 'error' | 'info';

interface Toast {
  id: number;
  message: string;
  type: ToastType;
}

const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: ToastType) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);

    // Auto dismiss
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, 3000);
  };

  return (
    <div className="p-8">
      <div className="space-x-4 mb-8">
        <button onClick={() => addToast('Success!', 'success')} className="px-4 py-2 bg-green-500 text-white rounded">Success</button>
        <button onClick={() => addToast('Error occurred', 'error')} className="px-4 py-2 bg-red-500 text-white rounded">Error</button>
        <button onClick={() => addToast('Info message', 'info')} className="px-4 py-2 bg-blue-500 text-white rounded">Info</button>
      </div>

      {/* Toast Container */}
      <div className="fixed top-4 right-4 z-50 flex flex-col gap-2">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`
              w-64 p-4 rounded-lg shadow-lg text-white transform transition-all duration-300 animate-slide-in
              ${toast.type === 'success' ? 'bg-green-500' : ''}
              ${toast.type === 'error' ? 'bg-red-500' : ''}
              ${toast.type === 'info' ? 'bg-blue-500' : ''}
            `}
          >
            <p className="font-medium">{toast.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

