import React, { useState } from 'react';

interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error';
}

export const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, 3000);
  };

  return (
    <div className="p-8 relative min-h-[200px]">
      <div className="space-x-4">
        <button 
          onClick={() => addToast('Changes saved successfully!', 'success')}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Show Success
        </button>
        <button 
          onClick={() => addToast('Failed to save data.', 'error')}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Show Error
        </button>
      </div>

      <div className="fixed bottom-4 right-4 flex flex-col space-y-2 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`px-4 py-3 rounded shadow-lg text-white text-sm font-medium transition-all animate-in slide-in-from-right-full duration-300 ${
              toast.type === 'success' ? 'bg-green-600' : 'bg-red-600'
            }`}
          >
            {toast.message}
          </div>
        ))}
      </div>
    </div>
  );
};

