import React, { useState } from 'react';

export const FormWizard: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const totalSteps = 3;

  const handleNext = () => {
    if (currentStep < totalSteps) setCurrentStep(prev => prev + 1);
  };

  const handleBack = () => {
    if (currentStep > 1) setCurrentStep(prev => prev - 1);
  };

  return (
    <div className="w-full max-w-2xl mx-auto p-6">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between mb-2">
          {Array.from({ length: totalSteps }).map((_, idx) => (
            <span key={idx} className={`text-sm font-medium ${idx + 1 <= currentStep ? 'text-blue-600' : 'text-gray-400'}`}>
              Step {idx + 1}
            </span>
          ))}
        </div>
        <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
          <div 
            className="h-full bg-blue-600 transition-all duration-300 ease-out"
            style={{ width: `${(currentStep / totalSteps) * 100}%` }}
          />
        </div>
      </div>

      {/* Content Area */}
      <div className="bg-white p-8 rounded-xl shadow-sm border border-gray-200 min-h-[200px] mb-6">
        {currentStep === 1 && <div><h2 className="text-xl font-bold mb-4">Account Details</h2><p>Form fields for step 1...</p></div>}
        {currentStep === 2 && <div><h2 className="text-xl font-bold mb-4">Personal Info</h2><p>Form fields for step 2...</p></div>}
        {currentStep === 3 && <div><h2 className="text-xl font-bold mb-4">Review & Submit</h2><p>Summary of data...</p></div>}
      </div>

      {/* Navigation */}
      <div className="flex justify-between">
        <button
          onClick={handleBack}
          disabled={currentStep === 1}
          className={`px-6 py-2 rounded-lg font-medium transition-colors ${currentStep === 1 ? 'text-gray-300 cursor-not-allowed' : 'text-gray-600 hover:bg-gray-100'}`}
        >
          Back
        </button>
        <button
          onClick={handleNext}
          className="px-6 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition-colors shadow-sm"
        >
          {currentStep === totalSteps ? 'Submit' : 'Next Step'}
        </button>
      </div>
    </div>
  );
};

