import React, { useState, KeyboardEvent } from 'react';

export const TagInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && input.trim()) {
      e.preventDefault();
      if (!tags.includes(input.trim())) {
        setTags([...tags, input.trim()]);
      }
      setInput('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-md">
      <label className="block text-sm font-medium text-gray-700 mb-2">Skills</label>
      <div className="flex flex-wrap items-center gap-2 p-2 border border-gray-300 rounded-lg focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-transparent bg-white">
        {tags.map((tag) => (
          <span key={tag} className="flex items-center gap-1 px-2 py-1 text-sm bg-blue-100 text-blue-800 rounded-md">
            {tag}
            <button 
              onClick={() => removeTag(tag)}
              className="hover:text-blue-900 focus:outline-none"
            >
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type and press Enter..."
          className="flex-1 min-w-[120px] outline-none bg-transparent text-sm py-1"
        />
      </div>
    </div>
  );
};

