import React, { useState, useEffect } from 'react';
import { CheckCircle, X } from 'lucide-react';

interface Toast {
  id: number;
  message: string;
}

const ToastNotification: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = () => {
    const id = Date.now();
    setToasts([...toasts, { id, message: `Action successful! (ID: ${id.toString().slice(-4)})` }]);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  useEffect(() => {
    if (toasts.length > 0) {
      const timer = setTimeout(() => {
        removeToast(toasts[0].id);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [toasts]);

  return (
    <div className="p-10 flex flex-col items-center">
      <button 
        onClick={addToast}
        className="bg-green-600 text-white px-6 py-2 rounded shadow hover:bg-green-700 transition"
      >
        Trigger Success Toast
      </button>

      <div className="fixed bottom-5 right-5 flex flex-col gap-3 z-50">
        {toasts.map((toast) => (
          <div 
            key={toast.id} 
            className="flex items-center gap-3 bg-white border-l-4 border-green-500 shadow-lg rounded-r p-4 min-w-[300px] animate-in slide-in-from-right duration-300"
          >
            <CheckCircle className="text-green-500" size={20} />
            <span className="text-sm text-gray-700 flex-1">{toast.message}</span>
            <button onClick={() => removeToast(toast.id)} className="text-gray-400 hover:text-gray-600">
              <X size={16} />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastNotification;

