import React, { useState, KeyboardEvent } from 'react';
import { X } from 'lucide-react';

const TagInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [inputValue, setInputValue] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      const trimmed = inputValue.trim();
      if (trimmed && !tags.includes(trimmed)) {
        setTags([...tags, trimmed]);
        setInputValue('');
      }
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter((tag) => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-lg mx-auto mt-10">
      <label className="block text-sm font-medium text-gray-700 mb-2">Add Skills</label>
      <div className="flex flex-wrap items-center gap-2 border border-gray-300 rounded-lg p-2 focus-within:ring-2 focus-within:ring-indigo-500 focus-within:border-indigo-500 bg-white">
        {tags.map((tag) => (
          <div
            key={tag}
            className="flex items-center bg-indigo-100 text-indigo-800 text-sm font-medium px-2.5 py-0.5 rounded"
          >
            {tag}
            <button
              onClick={() => removeTag(tag)}
              className="ml-1.5 text-indigo-600 hover:text-indigo-900 focus:outline-none"
            >
              <X size={14} />
            </button>
          </div>
        ))}
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-grow outline-none bg-transparent text-sm text-gray-700 placeholder-gray-400 min-w-[120px]"
          placeholder="Type and press Enter..."
        />
      </div>
      <p className="mt-2 text-xs text-gray-500">Press Enter to add a tag.</p>
    </div>
  );
};

