import React, { useState, KeyboardEvent } from 'react';

const TagInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>(['React', 'TypeScript']);
  const [inputValue, setInputValue] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && inputValue.trim() !== '') {
      e.preventDefault();
      if (!tags.includes(inputValue.trim())) {
        setTags([...tags, inputValue.trim()]);
      }
      setInputValue('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="max-w-md w-full p-2 border border-gray-300 rounded-lg bg-white flex flex-wrap gap-2 focus-within:ring-2 focus-within:ring-blue-500">
      {tags.map(tag => (
        <span key={tag} className="flex items-center px-2 py-1 bg-blue-100 text-blue-800 rounded-md text-sm font-medium">
          {tag}
          <button
            onClick={() => removeTag(tag)}
            className="ml-1 text-blue-600 hover:text-blue-800 focus:outline-none"
          >
            &times;
          </button>
        </span>
      ))}
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Add a tag..."
        className="flex-grow px-2 py-1 outline-none text-gray-700 min-w-[100px]"
      />
    </div>
  );
};

export default TagInput;

