interface StepperProps {
  currentStep: number;
  steps: string[];
}

const Stepper: React.FC<StepperProps> = ({ currentStep, steps }) => {
  return (
    <div className="w-full py-6">
      <div className="flex items-center w-full">
        {steps.map((step, index) => {
          const isCompleted = index < currentStep;
          const isCurrent = index === currentStep;

          return (
            <div key={index} className="flex-1 flex items-center relative">
              <div className="flex flex-col items-center relative z-10">
                <div
                  className={`w-8 h-8 rounded-full flex items-center justify-center border-2 transition-colors duration-300 ${
                    isCompleted || isCurrent
                      ? 'bg-blue-600 border-blue-600 text-white'
                      : 'bg-white border-gray-300 text-gray-500'
                  }`}
                >
                  {isCompleted ? '✓' : index + 1}
                </div>
                <div className={`absolute top-10 text-xs font-medium uppercase ${isCurrent ? 'text-blue-600' : 'text-gray-500'}`}>
                  {step}
                </div>
              </div>
              {index !== steps.length - 1 && (
                <div className={`flex-auto border-t-2 transition-colors duration-300 ${isCompleted ? 'border-blue-600' : 'border-gray-300'}`}></div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

