interface ToggleProps {
  label: string;
  initialState?: boolean;
  onChange?: (state: boolean) => void;
}

const ToggleSwitch: React.FC<ToggleProps> = ({ label, initialState = false, onChange }) => {
  const [isEnabled, setIsEnabled] = useState(initialState);

  const handleToggle = () => {
    const newState = !isEnabled;
    setIsEnabled(newState);
    if (onChange) onChange(newState);
  };

  return (
    <div className="flex items-center justify-between space-x-4 p-4">
      <span className="text-gray-700 font-medium">{label}</span>

      <button
        onClick={handleToggle}
        className={`relative inline-flex h-7 w-12 items-center rounded-full transition-colors duration-300 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 ${
          isEnabled ? 'bg-indigo-600' : 'bg-gray-300'
        }`}
        role="switch"
        aria-checked={isEnabled}
      >
        <span className="sr-only">Enable {label}</span>
        <span
          className={`inline-block h-5 w-5 transform rounded-full bg-white shadow-lg transition-transform duration-300 ease-in-out ${
            isEnabled ? 'translate-x-6' : 'translate-x-1'
          }`}
        />
      </button>
    </div>
  );
};

export default ToggleSwitch;

