import React, { useState, KeyboardEvent } from 'react';
import { X } from 'lucide-react';

const TagInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [inputValue, setInputValue] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && inputValue.trim() !== '') {
      e.preventDefault();
      if (!tags.includes(inputValue.trim())) {
        setTags([...tags, inputValue.trim()]);
      }
      setInputValue('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-md mx-auto mt-10">
      <label className="block text-sm font-medium text-gray-700 mb-2">Add Skills</label>
      <div className="flex flex-wrap gap-2 p-2 border border-gray-300 rounded-lg focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-transparent bg-white">
        {tags.map((tag, index) => (
          <span 
            key={index} 
            className="flex items-center gap-1 px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full"
          >
            {tag}
            <button 
              onClick={() => removeTag(tag)}
              className="hover:bg-blue-200 rounded-full p-0.5 transition"
            >
              <X size={14} />
            </button>
          </span>
        ))}
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type and press Enter..."
          className="flex-grow min-w-[120px] outline-none text-sm py-1 px-1 bg-transparent"
        />
      </div>
      <p className="text-xs text-gray-500 mt-2">Press Enter to add a tag.</p>
    </div>
  );
};

export default TagInput;

