import React, { useEffect } from 'react';

interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  children: React.ReactNode;
}

export const Modal: React.FC<ModalProps> = ({ isOpen, onClose, title, children }) => {
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
    };
    if (isOpen) window.addEventListener('keydown', handleEscape);
    return () => window.removeEventListener('keydown', handleEscape);
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center overflow-y-auto overflow-x-hidden bg-black/50 p-4 backdrop-blur-sm">
      <div 
        className="relative w-full max-w-md rounded-lg bg-white shadow-xl"
        onClick={(e) => e.stopPropagation()} // Prevent click from closing when clicking inside modal
      >
        <div className="flex items-center justify-between border-b p-4">
          <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
          <button
            onClick={onClose}
            className="ml-auto inline-flex items-center rounded-lg bg-transparent p-1.5 text-sm text-gray-400 hover:bg-gray-200 hover:text-gray-900"
          >
            ✕
          </button>
        </div>
        <div className="p-6 space-y-4">
          {children}
        </div>
        <div className="flex justify-end space-x-2 border-t p-4">
          <button
            onClick={onClose}
            className="rounded-lg border border-gray-300 px-5 py-2.5 text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-4 focus:ring-gray-200"
          >
            Close
          </button>
          <button
            className="rounded-lg bg-blue-700 px-5 py-2.5 text-sm font-medium text-white hover:bg-blue-800 focus:outline-none focus:ring-4 focus:ring-blue-300"
          >
            Confirm
          </button>
        </div>
      </div>
      {/* Backdrop click handler */}
      <div className="absolute inset-0 -z-10" onClick={onClose} />
    </div>
  );
};

import React, { useState, useEffect } from 'react';

interface SearchResult {
  id: number;
  name: string;
}

export const DebouncedSearch: React.FC = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<SearchResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (query) {
        setIsLoading(true);
        // Simulate API call
        setTimeout(() => {
          setResults([
            { id: 1, name: `${query} Result A` },
            { id: 2, name: `${query} Result B` },
            { id: 3, name: `${query} Result C` },
          ]);
          setIsLoading(false);
        }, 1000);
      } else {
        setResults([]);
      }
    }, 500); // 500ms debounce

    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="w-full max-w-md">
      <div className="relative">
        <input
          type="text"
          className="w-full rounded-lg border border-gray-300 bg-gray-50 p-4 pl-10 text-sm text-gray-900 focus:border-blue-500 focus:ring-blue-500"
          placeholder="Search items..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <div className="absolute inset-y-0 right-0 flex items-center pr-3">
          {isLoading && (
            <div className="h-5 w-5 animate-spin rounded-full border-2 border-blue-500 border-t-transparent"></div>
          )}
        </div>
      </div>

      {results.length > 0 && !isLoading && (
        <ul className="mt-2 divide-y divide-gray-100 rounded-lg border border-gray-200 bg-white shadow-sm">
          {results.map((item) => (
            <li key={item.id} className="cursor-pointer px-4 py-3 hover:bg-gray-50">
              {item.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

import React, { useState } from 'react';

interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error';
}

export const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
    setTimeout(() => removeToast(id), 3000);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  return (
    <div className="p-8">
      <div className="flex gap-4">
        <button
          onClick={() => addToast('Operation successful!', 'success')}
          className="rounded bg-green-600 px-4 py-2 text-white hover:bg-green-700"
        >
          Show Success
        </button>
        <button
          onClick={() => addToast('Something went wrong.', 'error')}
          className="rounded bg-red-600 px-4 py-2 text-white hover:bg-red-700"
        >
          Show Error
        </button>
      </div>

      <div className="fixed right-4 top-4 z-50 flex flex-col gap-2">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`flex min-w-[300px] items-center justify-between rounded-lg p-4 text-white shadow-lg transition-all duration-300 animate-in slide-in-from-right ${
              toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
            }`}
          >
            <span>{toast.message}</span>
            <button
              onClick={() => removeToast(toast.id)}
              className="ml-4 text-white/80 hover:text-white"
            >
              ✕
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

