          <span>{Math.round((step / totalSteps) * 100)}%</span>
        </div>
        <div className="h-2.5 w-full rounded-full bg-gray-200">
          <div
            className="h-2.5 rounded-full bg-blue-600 transition-all duration-300"
            style={{ width: `${(step / totalSteps) * 100}%` }}
          ></div>
        </div>
      </div>

      {/* Content */}
      <div className="mb-8 min-h-[150px]">
        {step === 1 && (
          <div className="space-y-4 animate-in fade-in slide-in-from-bottom-2">
            <h2 className="text-2xl font-bold">Account Info</h2>
            <input type="email" placeholder="Email" className="w-full rounded-md border p-2" />
            <input type="password" placeholder="Password" className="w-full rounded-md border p-2" />
          </div>
        )}
        {step === 2 && (
          <div className="space-y-4 animate-in fade-in slide-in-from-bottom-2">
            <h2 className="text-2xl font-bold">Personal Details</h2>
            <input type="text" placeholder="First Name" className="w-full rounded-md border p-2" />
            <input type="text" placeholder="Last Name" className="w-full rounded-md border p-2" />
          </div>
        )}
        {step === 3 && (
          <div className="space-y-4 animate-in fade-in slide-in-from-bottom-2">
            <h2 className="text-2xl font-bold">Review</h2>
            <p className="text-gray-600">Please review your information before submitting.</p>
            <div className="rounded bg-gray-50 p-4 text-sm">Summary details here...</div>
          </div>
        )}
      </div>

      {/* Controls */}
      <div className="flex justify-between">
        <button
          onClick={prevStep}
          disabled={step === 1}
          className="rounded-lg border border-gray-300 px-5 py-2.5 text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50"
