  const [currentStep, setCurrentStep] = useState(0);

  const handleNext = () => {
    if (currentStep < steps.length - 1) setCurrentStep((prev) => prev + 1);
  };

  const handleBack = () => {
    if (currentStep > 0) setCurrentStep((prev) => prev - 1);
  };

  const progress = ((currentStep + 1) / steps.length) * 100;

  return (
    <div className="max-w-xl mx-auto p-6 bg-white dark:bg-gray-800 rounded-xl shadow-lg">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between mb-2">
          {steps.map((step, index) => (
            <span 
              key={step} 
              className={`text-xs font-semibold uppercase ${index <= currentStep ? 'text-indigo-600' : 'text-gray-400'}`}
            >
              {step}
            </span>
          ))}
        </div>
        <div className="h-2 bg-gray-200 rounded-full">
          <div 
            className="h-full bg-indigo-600 rounded-full transition-all duration-300 ease-out"
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      </div>

      {/* Form Content */}
      <div className="min-h-[200px] flex items-center justify-center border-2 border-dashed border-gray-200 rounded-lg mb-6">
        <h2 className="text-2xl font-bold text-gray-700 dark:text-white">
          Step {currentStep + 1}: {steps[currentStep]}
        </h2>
      </div>

      {/* Navigation Buttons */}
      <div className="flex justify-between">
        <button
          onClick={handleBack}
          disabled={currentStep === 0}
          className={`px-4 py-2 rounded-lg font-medium transition-colors ${currentStep === 0 ? 'text-gray-400 cursor-not-allowed' : 'text-gray-600 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-700'}`}
        >
          Back
        </button>
        <button
          onClick={handleNext}
          disabled={currentStep === steps.length - 1}
          className={`px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-transform active:scale-95 disabled:opacity-50 disabled:cursor-not-allowed`}
        >
          {currentStep === steps.length - 1 ? 'Finish' : 'Next'}
        </button>
      </div>
    </div>
  );
};

