import React, { useState } from 'react';
import { Star } from 'lucide-react';

interface StarRatingProps {
  maxStars?: number;
  initialRating?: number;
  onRate?: (rating: number) => void;
}

export const StarRating: React.FC<StarRatingProps> = ({ maxStars = 5, initialRating = 0, onRate }) => {
  const [rating, setRating] = useState(initialRating);
  const [hover, setHover] = useState<number | null>(null);

  return (
    <div className="flex flex-col gap-2">
      <div className="flex gap-1">
        {[...Array(maxStars)].map((_, index) => {
          const starValue = index + 1;
          const isActive = starValue <= (hover || rating);

          return (
            <button
              key={index}
              type="button"
              className="focus:outline-none transition-transform hover:scale-110"
              onClick={() => {
                setRating(starValue);
                onRate?.(starValue);
              }}
              onMouseEnter={() => setHover(starValue)}
              onMouseLeave={() => setHover(null)}
            >
              <Star
                className={`w-8 h-8 transition-colors duration-200 ${
                  isActive 
                    ? 'fill-yellow-400 text-yellow-400' 
                    : 'fill-transparent text-gray-300'
                }`}
              />
            </button>
          );
        })}
      </div>
      <p className="text-sm font-medium text-gray-500">
        {hover ? `Rate ${hover} out of ${maxStars}` : `Current rating: ${rating}`}
      </p>
    </div>
  );
};

import React, { useState, DragEvent, ChangeEvent } from 'react';
import { UploadCloud, File, X } from 'lucide-react';

export const FileUpload: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [files, setFiles] = useState<File[]>([]);

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setIsDragging(true);
    } else if (e.type === 'dragleave') {
      setIsDragging(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFiles((prev) => [...prev, ...Array.from(e.dataTransfer.files)]);
    }
  };

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFiles((prev) => [...prev, ...Array.from(e.target.files!)]);
    }
  };

  const removeFile = (index: number) => {
    setFiles(files.filter((_, i) => i !== index));
  };

  return (
    <div className="w-full max-w-lg mx-auto">
      <div
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        className={`
          relative border-2 border-dashed rounded-xl p-8 text-center transition-all duration-200
          ${isDragging 
            ? 'border-blue-500 bg-blue-50' 
            : 'border-gray-300 bg-gray-50 hover:border-gray-400'}
        `}
      >
        <input
          type="file"
          multiple
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          onChange={handleChange}
        />
        <div className="flex flex-col items-center justify-center space-y-3">
          <div className={`p-3 rounded-full ${isDragging ? 'bg-blue-100 text-blue-600' : 'bg-gray-100 text-gray-500'}`}>
            <UploadCloud className="w-8 h-8" />
          </div>
          <div className="space-y-1">
            <p className="text-sm font-medium text-gray-700">
              <span className="text-blue-600">Click to upload</span> or drag and drop
            </p>
            <p className="text-xs text-gray-500">SVG, PNG, JPG or GIF (max. 5MB)</p>
          </div>
        </div>
      </div>

      {files.length > 0 && (
        <ul className="mt-6 space-y-3">
          {files.map((file, idx) => (
            <li key={idx} className="flex items-center justify-between p-3 bg-white border border-gray-200 rounded-lg shadow-sm">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-blue-50 rounded-lg">
                  <File className="w-5 h-5 text-blue-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-700 truncate max-w-[200px]">{file.name}</p>
                  <p className="text-xs text-gray-500">{(file.size / 1024).toFixed(1)} KB</p>
                </div>
              </div>
              <button 
                onClick={() => removeFile(idx)}
                className="p-1 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded-full transition-colors"
              >
                <X className="w-4 h-4" />
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { CheckCircle, AlertCircle, X } from 'lucide-react';

type ToastType = 'success' | 'error';

interface Toast {
  id: number;
  message: string;
  type: ToastType;
}

export const ToastContainer: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: ToastType) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  return (
    <div className="p-8">
      {/* Trigger Buttons for Demo */}
      <div className="flex gap-4 mb-8">
        <button
          onClick={() => addToast('Changes saved successfully!', 'success')}
          className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
        >
          Show Success
        </button>
        <button
          onClick={() => addToast('Failed to connect to server.', 'error')}
          className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
        >
          Show Error
        </button>
      </div>

      {/* Toast Feed */}
      <div className="fixed bottom-4 right-4 z-50 flex flex-col gap-3">
        {toasts.map((toast) => (
          <ToastItem key={toast.id} toast={toast} onClose={() => removeToast(toast.id)} />
        ))}
      </div>
    </div>
  );
};

const ToastItem: React.FC<{ toast: Toast; onClose: () => void }> = ({ toast, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div
      className={`
        flex items-center w-full max-w-xs p-4 rounded-lg shadow-lg transform transition-all duration-300 animate-in slide-in-from-right
        ${toast.type === 'success' ? 'bg-white border-l-4 border-green-500' : 'bg-white border-l-4 border-red-500'}
      `}
    >
      <div className="flex-shrink-0">
        {toast.type === 'success' ? (
          <CheckCircle className="w-5 h-5 text-green-500" />
        ) : (
          <AlertCircle className="w-5 h-5 text-red-500" />
        )}
      </div>
      <div className="ml-3 text-sm font-medium text-gray-800">{toast.message}</div>
      <button
        onClick={onClose}
        className="ml-auto -mx-1.5 -my-1.5 rounded-lg p-1.5 text-gray-400 hover:text-gray-900 focus:ring-2 focus:ring-gray-300"
      >
        <X className="w-4 h-4" />
      </button>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Search } from 'lucide-react';

// Mock data
const PEOPLE = [
  'Alice Johnson', 'Bob Smith', 'Charlie Brown', 'David Wilson', 
  'Eva Martinez', 'Frank Thomas', 'Grace Lee', 'Hannah White'
];

export const DebouncedSearch: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredResults, setFilteredResults] = useState(PEOPLE);
  const [isSearching, setIsSearching] = useState(false);

  useEffect(() => {
    setIsSearching(true);
    const handler = setTimeout(() => {
      const results = PEOPLE.filter(person =>
        person.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredResults(results);
      setIsSearching(false);
    }, 300); // 300ms debounce delay

    return () => {
      clearTimeout(handler);
    };
  }, [searchTerm]);

  return (
    <div className="w-full max-w-md mx-auto p-4">
      <div className="relative">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search className="h-5 w-5 text-gray-400" />
        </div>
        <input
          type="text"
          className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg leading-5 bg-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition-shadow"
          placeholder="Search people..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {isSearching && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            <div className="animate-spin h-4 w-4 border-2 border-blue-500 border-t-transparent rounded-full"></div>
          </div>
        )}
      </div>

      <div className="mt-4 bg-white rounded-lg shadow-sm border border-gray-100 overflow-hidden">
        {filteredResults.length > 0 ? (
          <ul className="divide-y divide-gray-100">
            {filteredResults.map((person, index) => (
              <li key={index} className="px-4 py-3 hover:bg-gray-50 text-gray-700 text-sm transition-colors">
                {person}
              </li>
            ))}
          </ul>
        ) : (
          <div className="px-4 py-6 text-center text-gray-500 text-sm">
            No results found for "{searchTerm}"
          </div>
        )}
      </div>
    </div>
  );
};

