            <input type="email" placeholder="Email Address" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
          </div>
        )}
        {step === 2 && (
          <div className="space-y-4 animate-in fade-in slide-in-from-right-4 duration-300">
            <h2 className="text-2xl font-bold text-gray-800">Account Details</h2>
            <input type="text" placeholder="Username" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
            <input type="password" placeholder="Password" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none" />
          </div>
        )}
        {step === 3 && (
          <div className="text-center space-y-4 animate-in fade-in slide-in-from-right-4 duration-300">
            <h2 className="text-2xl font-bold text-gray-800">Confirmation</h2>
            <p className="text-gray-600">Please review your details before submitting.</p>
