        <button
          onClick={step === 3 ? () => alert('Submitted!') : nextStep}
          className="px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 shadow-md shadow-indigo-200 transition-colors font-medium"
        >
          {step === 3 ? 'Submit' : 'Next Step'}
