import React, { useState, useEffect } from 'react';

interface ToastProps {
  message: string;
  type: 'success' | 'error';
  duration?: number;
  onClose: () => void;
}

export const Toast: React.FC<ToastProps> = ({ message, type, duration = 3000, onClose }) => {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(false);
      setTimeout(onClose, 300); // Wait for animation before unmounting
    }, duration);

    return () => clearTimeout(timer);
  }, [duration, onClose]);

  const bgClass = type === 'success' ? 'bg-green-500' : 'bg-red-500';

  return (
    <div
      className={`fixed bottom-5 right-5 px-6 py-3 rounded shadow-lg text-white transition-opacity duration-300 ${bgClass} ${isVisible ? 'opacity-100' : 'opacity-0'}`}
      role="alert"
    >
      <div className="flex items-center space-x-2">
        <span>{type === 'success' ? '✓' : '⚠'}</span>
        <span>{message}</span>
      </div>
    </div>
  );
};

