import React, { useState, DragEvent } from 'react';

export const FileDropzone: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [files, setFiles] = useState<File[]>([]);

  const handleDragOver = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      setFiles(Array.from(e.dataTransfer.files));
      e.dataTransfer.clearData();
    }
  };

  return (
    <div
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      className={`w-full h-48 border-2 border-dashed rounded-lg flex flex-col items-center justify-center transition-colors duration-200 ${
        isDragging ? 'border-blue-500 bg-blue-50' : 'border-gray-300 bg-gray-50'
      }`}
    >
      <p className="text-gray-500 mb-2">Drag & drop files here</p>
      {files.length > 0 && (
        <div className="text-sm text-green-600">
          {files.length} file(s) selected: {files.map(f => f.name).join(', ')}
        </div>
      )}
    </div>
  );
};

import React, { useState, KeyboardEvent } from 'react';

interface TagInputProps {
  initialTags?: string[];
  onTagsChange?: (tags: string[]) => void;
}

export const TagInput: React.FC<TagInputProps> = ({ initialTags = [], onTagsChange }) => {
  const [tags, setTags] = useState<string[]>(initialTags);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && input.trim()) {
      e.preventDefault();
      if (!tags.includes(input.trim())) {
        const newTags = [...tags, input.trim()];
        setTags(newTags);
        onTagsChange?.(newTags);
      }
      setInput('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    const newTags = tags.filter(tag => tag !== tagToRemove);
    setTags(newTags);
    onTagsChange?.(newTags);
  };

  return (
    <div className="w-full max-w-md">
      <label className="block text-sm font-medium text-gray-700 mb-2">Add Tags</label>
      <div className="flex flex-wrap gap-2 p-2 border border-gray-300 rounded-lg focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-transparent bg-white">
        {tags.map((tag) => (
          <span key={tag} className="flex items-center gap-1 px-3 py-1 text-sm text-blue-700 bg-blue-100 rounded-full">
            {tag}
            <button onClick={() => removeTag(tag)} className="hover:text-blue-900 focus:outline-none">
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-grow outline-none bg-transparent text-sm py-1"
          placeholder="Type and press Enter..."
        />
      </div>
    </div>
  );
};

