import React, { useState, useMemo } from 'react';

interface Option {
  id: string;
  label: string;
}

export const SearchableDropdown: React.FC<{ options: Option[] }> = ({ options }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');
  const [selected, setSelected] = useState<Option | null>(null);

  const filteredOptions = useMemo(() => {
    return options.filter(opt => 
      opt.label.toLowerCase().includes(search.toLowerCase())
    );
  }, [options, search]);

  return (
    <div className="relative w-72">
      <div 
        className="w-full border border-gray-300 rounded-lg bg-white px-3 py-2 flex items-center justify-between cursor-text focus-within:ring-2 focus-within:ring-indigo-500 focus-within:border-indigo-500"
        onClick={() => setIsOpen(true)}
      >
        <input
          type="text"
          className="w-full outline-none text-gray-700 placeholder-gray-400"
          placeholder="Select an option..."
          value={isOpen ? search : (selected?.label || '')}
          onChange={(e) => setSearch(e.target.value)}
          onFocus={() => setIsOpen(true)}
        />
      </div>

      {isOpen && (
        <ul className="absolute z-10 w-full mt-1 max-h-60 overflow-auto bg-white border border-gray-200 rounded-lg shadow-lg py-1">
          {filteredOptions.length === 0 ? (
            <li className="px-4 py-2 text-gray-500 text-sm">No results found</li>
          ) : (
            filteredOptions.map((opt) => (
              <li
                key={opt.id}
                className="px-4 py-2 hover:bg-indigo-50 text-gray-700 cursor-pointer text-sm"
                onClick={() => {
                  setSelected(opt);
                  setSearch('');
                  setIsOpen(false);
                }}
              >
                {opt.label}
              </li>
            ))
          )}
        </ul>
      )}

      {/* Overlay to close when clicking outside */}
      {isOpen && (
        <div 
          className="fixed inset-0 z-0"
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
};

import React, { useState } from 'react';
import { Star } from 'lucide-react';

interface StarRatingProps {
  totalStars?: number;
  initialRating?: number;
  onRate?: (rating: number) => void;
}

export const StarRating: React.FC<StarRatingProps> = ({ totalStars = 5, initialRating = 0, onRate }) => {
  const [rating, setRating] = useState(initialRating);
  const [hover, setHover] = useState(0);

  return (
    <div className="flex items-center space-x-1">
      {[...Array(totalStars)].map((_, index) => {
        const starValue = index + 1;
        return (
          <button
            key={index}
            type="button"
            className="focus:outline-none transition-transform hover:scale-110"
            onClick={() => {
              setRating(starValue);
              if (onRate) onRate(starValue);
            }}
            onMouseEnter={() => setHover(starValue)}
            onMouseLeave={() => setHover(0)}
          >
            <Star
              size={24}
              className={`transition-colors duration-200 ${starValue <= (hover || rating) ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}`}
            />
          </button>
        );
      })}
      <span className="ml-2 text-sm text-gray-500 font-medium">
        {rating}/{totalStars}
      </span>
    </div>
  );
};

import React, { useState, useEffect } from 'react';

interface ToastProps {
  message: string;
  type: 'success' | 'error' | 'info';
  onClose: () => void;
}

const Toast: React.FC<ToastProps> = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  const styles = {
    success: 'bg-green-50 text-green-800 border-green-200',
    error: 'bg-red-50 text-red-800 border-red-200',
    info: 'bg-blue-50 text-blue-800 border-blue-200',
  };

  return (
    <div className={`flex items-center w-full max-w-xs p-4 mb-4 rounded-lg shadow border ${styles[type]} animate-in slide-in-from-right duration-300`}>
      <div className="text-sm font-normal">{message}</div>
      <button onClick={onClose} className="ml-auto -mx-1.5 -my-1.5 rounded-lg p-1.5 inline-flex h-8 w-8 hover:bg-black/5">
        <span className="sr-only">Close</span>
        &times;
      </button>
    </div>
  );
};

// Usage Example Container
export const ToastContainer: React.FC = () => {
  const [toasts, setToasts] = useState<{id: number, msg: string}[]>([]);

  const addToast = () => {
    const id = Date.now();
    setToasts([...toasts, { id, msg: `Notification ${id}` }]);
  };

  const removeToast = (id: number) => {
    setToasts(prev => prev.filter(t => t.id !== id));
  };

  return (
    <div className="p-8">
      <button onClick={addToast} className="bg-blue-600 text-white px-4 py-2 rounded">Show Toast</button>

      <div className="fixed top-5 right-5 z-50 flex flex-col gap-2">
        {toasts.map(t => (
          <Toast 
            key={t.id} 
            message={t.msg} 
            type="success" 
            onClose={() => removeToast(t.id)} 
          />
        ))}
      </div>
    </div>
  );
};

import React from 'react';

interface ProgressBarProps {
  currentStep: number; // 1-based index
  totalSteps: number;
}

export const ProgressBar: React.FC<ProgressBarProps> = ({ currentStep, totalSteps }) => {
  const steps = Array.from({ length: totalSteps }, (_, i) => i + 1);

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="relative flex items-center justify-between">
        {/* Background Line */}
        <div className="absolute left-0 top-1/2 transform -translate-y-1/2 w-full h-1 bg-gray-200 -z-10" />

        {/* Active Line Fill */}
        <div 
          className="absolute left-0 top-1/2 transform -translate-y-1/2 h-1 bg-indigo-600 -z-10 transition-all duration-300"
          style={{ width: `${((currentStep - 1) / (totalSteps - 1)) * 100}%` }}
        />

        {steps.map((step) => {
          const isActive = step <= currentStep;
          const isCompleted = step < currentStep;

          return (
            <div key={step} className="flex flex-col items-center">
              <div 
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center font-bold text-sm border-2 transition-colors duration-300
                  ${isActive 
                    ? 'bg-indigo-600 border-indigo-600 text-white' 
                    : 'bg-white border-gray-300 text-gray-500'}
                `}
              >
                {isCompleted ? '✓' : step}
              </div>
              <span className={`mt-2 text-xs font-medium ${isActive ? 'text-indigo-600' : 'text-gray-400'}`}>
                Step {step}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

import React, { useState } from 'react';

export const NewsletterSignup: React.FC = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle');
  const [message, setMessage] = useState('');

  const validateEmail = (email: string) => {
    return String(email)
      .toLowerCase()
      .match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validateEmail(email)) {
      setStatus('error');
      setMessage('Please enter a valid email address.');
      return;
    }

    setStatus('loading');
    // Simulate API call
    setTimeout(() => {
      setStatus('success');
      setMessage('Thanks for subscribing!');
      setEmail('');
    }, 1500);
  };

  return (
    <div className="bg-gray-50 p-8 rounded-2xl max-w-md mx-auto border border-gray-200">
      <h2 className="text-2xl font-bold text-gray-900 mb-2">Subscribe to our newsletter</h2>
      <p className="text-gray-600 mb-6">Get the latest updates directly to your inbox.</p>

      <form onSubmit={handleSubmit} className="flex flex-col gap-3">
        <div>
          <label htmlFor="email" className="sr-only">Email address</label>
          <input
            id="email"
            type="email"
            placeholder="you@example.com"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
              if (status === 'error') setStatus('idle');
            }}
            disabled={status === 'success' || status === 'loading'}
            className={`
              w-full px-4 py-3 rounded-lg border focus:ring-2 focus:outline-none transition-colors
              ${status === 'error' 
                ? 'border-red-300 focus:border-red-500 focus:ring-red-200 bg-red-50' 
                : 'border-gray-300 focus:border-indigo-500 focus:ring-indigo-200'}
            `}
          />
          {status === 'error' && (
            <p className="mt-2 text-sm text-red-600">{message}</p>
          )}
        </div>

        <button
          type="submit"
          disabled={status === 'loading' || status === 'success'}
          className={`
            w-full py-3 px-4 rounded-lg font-semibold text-white transition-all
            ${status === 'success' 
              ? 'bg-green-600 hover:bg-green-700' 
              : 'bg-indigo-600 hover:bg-indigo-700'}
            disabled:opacity-70 disabled:cursor-not-allowed
          `}
        >
          {status === 'loading' ? 'Subscribing...' : status === 'success' ? 'Subscribed!' : 'Subscribe'}
        </button>
      </form>
    </div>
  );
};

