import React, { useState, KeyboardEvent } from 'react';
import { X } from 'lucide-react';

export const TagsInput: React.FC = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && input.trim()) {
      e.preventDefault();
      if (!tags.includes(input.trim())) {
        setTags([...tags, input.trim()]);
      }
      setInput('');
    } else if (e.key === 'Backspace' && !input && tags.length > 0) {
      setTags(tags.slice(0, -1));
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="flex flex-wrap items-center gap-2 rounded-md border border-gray-300 bg-white p-2 focus-within:ring-2 focus-within:ring-indigo-500 focus-within:border-indigo-500">
      {tags.map((tag) => (
        <span key={tag} className="inline-flex items-center gap-1 rounded-full bg-indigo-100 px-2.5 py-0.5 text-sm font-medium text-indigo-800">
          {tag}
          <button onClick={() => removeTag(tag)} className="text-indigo-600 hover:text-indigo-900">
            <X size={14} />
          </button>
        </span>
      ))}
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        className="flex-1 min-w-[120px] bg-transparent outline-none text-sm text-gray-700 placeholder-gray-400"
        placeholder="Type and press Enter..."
      />
    </div>
  );
};

import React from 'react';
import { Check } from 'lucide-react';

interface StepperProps {
  currentStep: number;
  steps: string[];
}

export const Stepper: React.FC<StepperProps> = ({ currentStep, steps }) => {
  return (
    <div className="w-full py-4">
      <div className="flex items-center justify-between">
        {steps.map((step, index) => {
          const stepNumber = index + 1;
          const isCompleted = currentStep > stepNumber;
          const isCurrent = currentStep === stepNumber;

          return (
            <div key={step} className="flex flex-1 flex-col items-center relative">
              {/* Connector Line */}
              {index !== 0 && (
                <div 
                  className={`absolute top-4 right-1/2 -mr-4 h-0.5 w-full -translate-y-1/2 transition-colors duration-300 
                  ${index < currentStep ? 'bg-indigo-600' : 'bg-gray-200'}`}
                />
              )}

              <div
                className={`
                  relative z-10 flex h-8 w-8 items-center justify-center rounded-full border-2 transition-all duration-300
                  ${isCompleted ? 'border-indigo-600 bg-indigo-600 text-white' : ''}
                  ${isCurrent ? 'border-indigo-600 bg-white text-indigo-600' : ''}
                  ${!isCompleted && !isCurrent ? 'border-gray-300 bg-white text-gray-500' : ''}
                `}
              >
                {isCompleted ? <Check size={16} /> : <span className="text-xs font-bold">{stepNumber}</span>}
              </div>
              <span 
                className={`mt-2 text-xs font-medium transition-colors duration-300
                ${isCurrent || isCompleted ? 'text-indigo-600' : 'text-gray-500'}`}
              >
                {step}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

