import React from 'react';

interface StepperProps {
  currentStep: number; // 1-based index
  totalSteps: number;
}

export const Stepper: React.FC<StepperProps> = ({ currentStep, totalSteps }) => {
  const steps = Array.from({ length: totalSteps }, (_, i) => i + 1);

  return (
    <div className="flex items-center w-full max-w-2xl mx-auto p-4">
      {steps.map((step, index) => {
        const isCompleted = step < currentStep;
        const isActive = step === currentStep;
        const isLast = index === steps.length - 1;

        return (
          <React.Fragment key={step}>
            <div className="relative flex items-center">
              <div 
                className={`flex items-center justify-center w-10 h-10 rounded-full border-2 font-semibold transition-colors duration-300 
                ${isCompleted ? 'bg-blue-600 border-blue-600 text-white' : 
                  isActive ? 'bg-white border-blue-600 text-blue-600' : 
                  'bg-white border-gray-300 text-gray-400'}`}
              >
                {isCompleted ? '✓' : step}
              </div>
              {isActive && (
                <div className="absolute -bottom-6 w-max text-xs font-medium text-blue-600">
                  Step {step}
                </div>
              )}
            </div>
            {!isLast && (
              <div className="flex-auto border-t-2 transition-colors duration-300 mx-2" 
                   style={{ borderColor: isCompleted ? '#2563EB' : '#E5E7EB' }} />
            )}
          </React.Fragment>
        );
      })}
    </div>
  );
};

import React, { useState, useRef, useEffect } from 'react';

interface AutocompleteProps {
  options: string[];
  placeholder?: string;
}

export const Autocomplete: React.FC<AutocompleteProps> = ({ options, placeholder = 'Search...' }) => {
  const [query, setQuery] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const wrapperRef = useRef<HTMLDivElement>(null);

  const filteredOptions = options.filter(opt => 
    opt.toLowerCase().includes(query.toLowerCase())
  );

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div ref={wrapperRef} className="relative w-full max-w-xs">
      <input
        type="text"
        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none"
        placeholder={placeholder}
        value={query}
        onChange={(e) => { setQuery(e.target.value); setIsOpen(true); }}
        onFocus={() => setIsOpen(true)}
      />
      {isOpen && filteredOptions.length > 0 && (
        <ul className="absolute z-10 w-full mt-1 bg-white border border-gray-200 rounded-lg shadow-lg max-h-60 overflow-auto">
          {filteredOptions.map((option, idx) => (
            <li
              key={idx}
              onClick={() => { setQuery(option); setIsOpen(false); }}
              className="px-4 py-2 hover:bg-blue-50 cursor-pointer text-gray-700"
            >
              {option}
            </li>
          ))}
        </ul>
      )}
      {isOpen && filteredOptions.length === 0 && (
        <div className="absolute z-10 w-full mt-1 bg-white border border-gray-200 rounded-lg shadow-lg p-2 text-gray-500 text-sm text-center">
          No results found
        </div>
      )}
    </div>
  );
};

import React, { useState, DragEvent, ChangeEvent } from 'react';
import { UploadCloud } from 'lucide-react';

export const FileUpload: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [file, setFile] = useState<File | null>(null);

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setIsDragging(true);
    } else if (e.type === 'dragleave') {
      setIsDragging(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  return (
    <div className="w-full max-w-md mx-auto">
      <div
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        className={`relative flex flex-col items-center justify-center h-64 border-2 border-dashed rounded-xl transition-colors duration-200 
          ${isDragging ? 'border-blue-500 bg-blue-50' : 'border-gray-300 bg-gray-50'}`}
      >
        <input
          type="file"
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          onChange={handleChange}
        />
        <div className="flex flex-col items-center text-center p-4">
          <UploadCloud className={`w-12 h-12 mb-3 ${isDragging ? 'text-blue-500' : 'text-gray-400'}`} />
          {file ? (
            <div className="text-sm text-gray-700">
              <span className="font-semibold">Selected:</span> {file.name}
            </div>
          ) : (
            <>
              <p className="text-lg font-medium text-gray-700">Drop your file here</p>
              <p className="text-sm text-gray-500 mt-1">or click to browse</p>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

