import React, { useState, DragEvent, ChangeEvent } from 'react';
import { UploadCloud, File } from 'lucide-react';

export const FileUploader = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [file, setFile] = useState<File | null>(null);

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setIsDragging(true);
    } else if (e.type === 'dragleave') {
      setIsDragging(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  return (
    <div className="w-full max-w-md mx-auto">
      <div
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        className={`relative border-2 border-dashed rounded-xl p-8 text-center transition-colors duration-200 ${
          isDragging 
            ? 'border-blue-500 bg-blue-50' 
            : 'border-gray-300 hover:border-gray-400 bg-white'
        }`}
      >
        <input
          type="file"
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          onChange={handleChange}
        />

        <div className="flex flex-col items-center justify-center space-y-3">
          {file ? (
            <>
              <div className="p-3 bg-blue-100 rounded-full text-blue-600">
                <File size={32} />
              </div>
              <div className="text-sm text-gray-700 font-medium">
                {file.name}
              </div>
              <button 
                onClick={(e) => { e.preventDefault(); setFile(null); }}
                className="text-xs text-red-500 hover:text-red-700 z-10"
              >
                Remove file
              </button>
            </>
          ) : (
            <>
              <div className="p-3 bg-gray-100 rounded-full text-gray-500">
                <UploadCloud size={32} />
              </div>
              <div className="space-y-1">
                <p className="text-sm font-medium text-gray-700">
                  Click to upload or drag and drop
                </p>
                <p className="text-xs text-gray-500">
                  SVG, PNG, JPG or GIF (max. 3MB)
                </p>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

import React, { useState, KeyboardEvent } from 'react';
import { X } from 'lucide-react';

export const TagInput = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      const trimmedInput = input.trim();
      if (trimmedInput && !tags.includes(trimmedInput)) {
        setTags([...tags, trimmedInput]);
        setInput('');
      }
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-md">
      <label className="block text-sm font-medium text-gray-700 mb-2">Add Tags</label>
      <div className="flex flex-wrap gap-2 p-2 border border-gray-300 rounded-lg bg-white focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-blue-500">
        {tags.map((tag) => (
          <span 
            key={tag} 
            className="flex items-center gap-1 px-2.5 py-1 rounded-md text-sm bg-blue-100 text-blue-800"
          >
            {tag}
            <button 
              onClick={() => removeTag(tag)}
              className="hover:text-blue-900 focus:outline-none"
            >
              <X size={14} />
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-1 min-w-[120px] outline-none text-sm bg-transparent py-1"
          placeholder="Type and press Enter..."
        />
      </div>
    </div>
  );
};

