import React, { useState, KeyboardEvent } from 'react';

export const TagInput = () => {
  const [tags, setTags] = useState<string[]>(['React', 'Tailwind']);
  const [input, setInput] = useState('');

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && input.trim()) {
      e.preventDefault();
      if (!tags.includes(input.trim())) {
        setTags([...tags, input.trim()]);
      }
      setInput('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  return (
    <div className="w-full max-w-md p-4 bg-white rounded-lg shadow-sm border border-gray-200">
      <label className="block text-sm font-medium text-gray-700 mb-2">Add Tags</label>
      <div className="flex flex-wrap gap-2 p-2 border border-gray-300 rounded-md focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-transparent">
        {tags.map(tag => (
          <span key={tag} className="flex items-center gap-1 px-2 py-1 text-sm text-blue-800 bg-blue-100 rounded-md">
            {tag}
            <button onClick={() => removeTag(tag)} className="hover:text-blue-600 focus:outline-none">
              &times;
            </button>
          </span>
        ))}
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-grow min-w-[100px] outline-none text-sm bg-transparent"
          placeholder="Type and press Enter..."
        />
      </div>
    </div>
  );
};

