import React, { useState, ChangeEvent, DragEvent } from 'react';
import { UploadCloud, File } from 'lucide-react';

export const FileUpload: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [fileName, setFileName] = useState<string | null>(null);

  const handleDragOver = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => setIsDragging(false);

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFileName(e.dataTransfer.files[0].name);
    }
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFileName(e.target.files[0].name);
    }
  };

  return (
    <div className="w-full max-w-md">
      <div
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        className={`relative flex flex-col items-center justify-center rounded-lg border-2 border-dashed p-10 transition-colors ${
          isDragging
            ? 'border-indigo-500 bg-indigo-50'
            : 'border-gray-300 bg-gray-50 hover:bg-gray-100'
        }`}
      >
        <input
          type="file"
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          onChange={handleFileChange}
        />
        {fileName ? (
          <div className="flex flex-col items-center text-indigo-600">
            <File size={40} className="mb-3" />
            <span className="text-sm font-medium">{fileName}</span>
            <span className="text-xs text-gray-500 mt-1">Click to change</span>
          </div>
        ) : (
          <div className="flex flex-col items-center text-gray-500">
            <UploadCloud size={40} className="mb-3" />
            <p className="mb-2 text-sm font-semibold">
              <span className="text-indigo-600">Click to upload</span> or drag and drop
            </p>
            <p className="text-xs">SVG, PNG, JPG or GIF (MAX. 800x400px)</p>
          </div>
        )}
      </div>
    </div>
  );
};

