import React, { createContext, useContext, useState, useCallback } from 'react';

// 1. Define Types
interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error' | 'info';
}

interface ToastContextType {
  addToast: (message: string, type: Toast['type']) => void;
}

// 2. Create Context
const ToastContext = createContext<ToastContextType | undefined>(undefined);

// 3. Provider Component
export const ToastProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = useCallback((message: string, type: Toast['type']) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);
    // Auto remove after 3 seconds
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, 3000);
  }, []);

  return (
    <ToastContext.Provider value={{ addToast }}>
      {children}
      {/* Toast Container */}
      <div className="fixed bottom-4 right-4 space-y-2 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`flex items-center w-full max-w-xs p-4 rounded-lg shadow-lg text-white transition-all transform translate-y-0 opacity-100 ${
              toast.type === 'success' ? 'bg-green-500' : 
              toast.type === 'error' ? 'bg-red-500' : 'bg-blue-500'
            }`}
            role="alert"
          >
            <div className="text-sm font-normal">{toast.message}</div>
          </div>
        ))}
      </div>
    </ToastContext.Provider>
  );
};

// 4. Custom Hook
export const useToast = () => {
  const context = useContext(ToastContext);
  if (!context) throw new Error('useToast must be used within a ToastProvider');
  return context;
};

// 5. Example Usage Component
export const ToastDemo: React.FC = () => {
  const { addToast } = useToast();
  return (
    <div className="flex gap-4 p-4">
      <button 
        onClick={() => addToast('Operation successful!', 'success')}
        className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
      >
        Success Toast
      </button>
      <button 
        onClick={() => addToast('Something went wrong.', 'error')}
        className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
      >
        Error Toast
      </button>
    </div>
  );
};

