import React, { useState } from 'react';

interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error';
}

const ToastSystem: React.FC = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);

    // Auto remove after 3 seconds
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, 3000);
  };

  return (
    <div className="min-h-screen p-8 bg-gray-50 flex flex-col items-center gap-4">
      {/* Triggers */}
      <div className="space-x-4">
        <button
          onClick={() => addToast('Operation Successful!', 'success')}
          className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
        >
          Show Success
        </button>
        <button
          onClick={() => addToast('Something went wrong.', 'error')}
          className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
        >
          Show Error
        </button>
      </div>

      {/* Toast Container */}
      <div className="fixed bottom-5 right-5 flex flex-col gap-2 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`
              min-w-[250px] px-4 py-3 rounded shadow-lg text-white transform transition-all duration-500 ease-in-out
              ${toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'}
              animate-slide-in-right
            `}
          >
            <div className="flex justify-between items-center">
              <span>{toast.message}</span>
              <button
                onClick={() => setToasts((prev) => prev.filter((t) => t.id !== toast.id))}
                className="ml-4 text-white hover:text-gray-200 font-bold"
              >
                &times;
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

